import React, {useState, useEffect} from "react";
import styled from 'styled-components';
import 'bootstrap/dist/css/bootstrap.min.css';
import {ContentContainer, ShadowLine, TitleArea} from "../../components/layout/Layout.Styled";
import ProgressBar from "react-bootstrap/ProgressBar";




function Resume(props) {

  const {} = props;

  return (
      <Container>
          <ContentContainer>
          <TitleArea>
              <p>MY RESUME</p>
              <h2>
                  MY EXPERTISES
                  <ShadowLine mt={30}/>
              </h2>
          </TitleArea>
              <div className="expertises">
                  <div className="item col-half">
                      <h3>My Experience</h3>
                      <ul>
                          <li>
                              <div className="card-item">
                                  <h5>Senior Architect</h5>
                                  <h6>2015 - Present | Google</h6>
                                  <p>Collaborate with creative and development teams on the execution of ideas.</p>
                              </div>
                          </li>
                          <li>
                              <div className="card-item">
                                  <h5>Senior Architect</h5>
                                  <h6>2015 - Present | Google</h6>
                                  <p>Collaborate with creative and development teams on the execution of ideas.</p>
                              </div>
                          </li>
                      </ul>
                  </div>
                  <div className="item col-half">
                      <h3>My Education</h3>
                      <ul>
                          <li>
                              <div className="card-item">
                                  <h5>Senior Architect</h5>
                                  <h6>2015 - Present | Google</h6>
                                  <p>Collaborate with creative and development teams on the execution of ideas.</p>
                              </div>
                          </li>
                          <li>
                              <div className="card-item">
                                  <h5>Senior Architect</h5>
                                  <h6>2015 - Present | Google</h6>
                                  <p>Collaborate with creative and development teams on the execution of ideas.</p>
                              </div>
                          </li>
                      </ul>
                  </div>
                  <div className="item col-full">
                      <h3>skills</h3>
                      <ul>
                          <li>
                              <div className="skill">
                                  <h6>React</h6>
                                  <div className="progress-bar">
                                      <ProgressBar animated now={30} label={`${30}%`}></ProgressBar>
                                  </div>
                              </div>
                          </li>
                          <li>
                              <div className="skill">
                                  <h6>React</h6>
                                  <div className="progress-bar">
                                      <ProgressBar animated now={30} label={`${30}%`}></ProgressBar>
                                  </div>
                              </div>
                          </li>
                          <li>
                              <div className="skill">
                                  <h6>React</h6>
                                  <div className="progress-bar">
                                      <ProgressBar animated now={30} label={`${30}%`}></ProgressBar>
                                  </div>
                              </div>
                          </li>
                          <li>
                              <div className="skill">
                                  <h6>React</h6>
                                  <div className="progress-bar">
                                      <ProgressBar animated now={30} label={`${30}%`}></ProgressBar>
                                  </div>
                              </div>
                          </li>
                      </ul>
                  </div>

              </div>
          </ContentContainer>


      </Container>
  )

}

const Container = styled.div`

  .expertises{
      display: flex;
      flex-wrap: wrap;
      margin-left: -15px;
      margin-right: -15px;
      .col-half{
          width: 50%;
          padding: 15px;
      }
      .col-full{
          width: 100%;
          padding: 15px;
      }

      .card-item{
          background:#fff;
          padding: 24px;
          border: #ddd;
          h5{
              font-size: 18px;
              color: #333;
              margin-bottom: 15px;
          }
          h6{
              font-size: 12px;
              background-color: #fd735a;
              display: inline-block;
              color: #ffff;
              padding: 2px 8px 0;
              border-radius: 3px;
              font-weight: 600;
          }
          p{

          }
      }
  }

`;

const ResumeContainer = styled.div`
  display: flex;
  justify-content: center;
  .title{
    font-size: 20px;
    margin: 16px 0 20px; 
  }
  .experience{
    float: left;
    padding: 0 15px;
  }
  .education{
  display: inline-block;
  padding: 0 15px;
  }
  .card{
    align-items: flex-start;
    background:#ffffff;
    height: 177px;
    width: 100%;
    margin: 24px 0 0;
    padding: 24px;
    overflow: hidden;
    transition: 0.4s;
    &:hover{
      box-shadow:  1px 2px 30px rgba(0,0,0,0.2);
   }
   }
   .skills{
    background:#ffffff;
    height: 310px;
    width: 100%;
    margin-top: 24px;
    margin-bottom: 80px; 
    padding: 24px;
    overflow: hidden;
    transition: 0.4s;
    &:hover{
      box-shadow:  1px 2px 30px rgba(0,0,0,0.2);
   }
   .skill{
      .progress-bar{
        width: 100%;
        height: 15px;
        text-align: center;
        //background:#fd735a;;
       }
      }
   }
    h5{
    font-size: 18px;
    }
    h6{
      //width: 30%;
      font-size: 12px;
      margin: 16px 0;
      padding: 3px 4.8px;
      background:#fd735a!important;
      display: inline-block!important;
      border-radius: 5px;
      //text-align: center;
    }
    p{
      font-size: 16px;
    }
  }


`;


export default Resume;
