import styled from 'styled-components';

export const PageContainer = styled.div`
    padding: 80px 0 300px;
`;

export const ContentContainer = styled.div`
  max-width: 1130px;
  margin: 0 auto;
  padding: 0 15px;    
`;

export const Overlay = styled.div`
  position: absolute;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  background: rgba(0,0,0,0.4);
  ${(props)=> props.alpha && `
    background: rgba(0,0,0,0.4);
  `}

`;
